function Computer_login_password_page () {
    Username = game.askForString("Username", 12)
    Password = game.askForNumber("Password", 10)
    if (Username == "1" && Password == 1) {
        game.showLongText("Correct", DialogLayout.Bottom)
        game.showLongText("Loding", DialogLayout.Bottom)
        Computer()
    } else {
        game.showLongText("Incorrect", DialogLayout.Bottom)
        Computer_login_page()
    }
}
function Computer_login_page () {
    scene.setBackgroundColor(15)
    game.showLongText("Computer login", DialogLayout.Bottom)
    story.showPlayerChoices("Login", "")
    if (story.getLastAnswer() == "Login") {
        Computer_login_password_page()
    }
}
